mapper.select:
select no, id, title, content, hit, regtime
from qna
where no = #{no}

mapper.update:
update qna
set title = #{title}, content = #{content}
where no = #{no}

update qna
set hit = hit + 1
where no = #{no}

mapper.select:
select replyno, no, content, regtime
from reply
where no = #{no}

mapper.update:
update reply
set content=#{content}
where no=#{no}
