mapper.select:
select *
from user
where id = #{id} and password = #{password}

select count(id)
from user
where id=#{id}

select id,password,name,address,number
from user
where id=#{id}

mapper.update:
update user
password = #{password}, address = #{address}, number = #{number},
where id = #{id}

update user
set accessToken = #{tokens.accessToken},
refreshToken = #{tokens.refreshToken}
where id = #{id}

mapper.select:
select count(id)
from user
where id=#{id}
and accessToken=#{accessToken}
and refreshToken=#{refreshToken}
